func Test(t *testing.T) {
	RegisterFailHandler(Fail)
	RunSpecs(t, "")
}

// test helpers

func testHelperSetETCDMembers(members []*etcdserverpb.Member) {
	mems := make([]*etcdserverpb.Member, len(members))
	for i, member := range members {
		a := *member
		mems[i] = &a
	}
	dependency.TestDC.EtcdClient.MemberListMock.Set(func(_ context.Context) (mp1 *clientv3.MemberListResponse, err error) {
		return &clientv3.MemberListResponse{
			Members: mems,
		}, nil
	})
}

func testHelperRegisterEtcdMemberUpdate() {
	dependency.TestDC.EtcdClient.MemberUpdateMock.Set(func(ctx context.Context, id uint64, peers []string) (mp1 *clientv3.MemberUpdateResponse, err error) {
		resp, _ := dependency.TestDC.EtcdClient.MemberList(ctx)
		members := resp.Members
		for i, member := range members {
			if member.ID != id {
				continue
			}
			member.PeerURLs = peers
			members[i] = member
			break
		}

		testHelperSetETCDMembers(members)
		return nil, nil
	})

	dependency.TestDC.EtcdClient.MemberRemoveMock.Set(func(ctx context.Context, id uint64) (mp1 *clientv3.MemberRemoveResponse, err error) {
		resp, _ := dependency.TestDC.EtcdClient.MemberList(ctx)
		members := resp.Members
		var index int
		for i, member := range members {
			if member.ID != id {
				continue
			}
			index = i
			break
		}
		members = append(members[:index], members[index+1:]...)
		testHelperSetETCDMembers(members)

		return &clientv3.MemberRemoveResponse{Members: members}, nil
	})

	dependency.TestDC.EtcdClient.CloseMock.Return(nil)
}

func etcdPodManifest(data map[string]interface{}) string {
	podTpl := `
apiVersion: v1
kind: Pod
metadata:
  creationTimestamp: "2022-04-05T16:52:43Z"
  labels:
    component: etcd
    tier: control-plane
  name: {{ .name }}
  namespace: kube-system
spec:
  containers:
  - command:
    - etcd
    {{- if .maxDbSize }}
    - --quota-backend-bytes={{ .maxDbSize }}
    {{- end }}
    image: registry.deckhouse.io/sys/deckhouse-oss:c47d237beff7354f150a2cd3aec929128c234b33b4b6fe938d5ab72a-1638791306829
    name: etcd
  {{- if not .podIP }}
  hostNetwork: true
  {{- end }}
status:
  hostIP: {{ .hostIP }}
  phase: Running
  {{- if .podIP }}
  podIP: {{ .podIP }}
  {{- else }}
  podIP: {{ .hostIP }}
  {{- end }}
  podIPs:
  {{- if .podIP }}
  - ip: {{ .podIP }}
  {{- else }}
  - ip: {{ .hostIP }}
  {{- end }}
  startTime: "2022-04-05T15:49:45Z"
`
	t := template.New("testetcd_pod_template")
	t, err := t.Parse(podTpl)
	if err != nil {
		panic(err)
	}

	var tpl bytes.Buffer

	err = t.Execute(&tpl, data)
	if err != nil {
		panic(err)
	}

	return tpl.String()
}

