package hooks

import (
	"context"
	"fmt"

	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
	corev1 "k8s.io/api/core/v1"
	v1 "k8s.io/apimachinery/pkg/apis/meta/v1"

	"github.com/deckhouse/deckhouse/go_lib/dependency"
	. "github.com/deckhouse/deckhouse/testing/helm"
	. "github.com/deckhouse/deckhouse/testing/hooks"
)

var _ = Describe("User Authn hooks :: generate basic auth proxy ::", func() {
	f := HookExecutionConfigInit(`{"userAuthn":{"internal": {"providers": [{
  "type": "Crowd",
  "displayName": "Crowd",
  "crowd": {
    "baseURL": "https://crowd.example.com/crowd",
    "clientID": "plainstring",
    "clientSecret": "plainstring",
    "enableBasicAuth": true,
    "groups": [
      "administrators",
      "users"
    ]
  }
}]}, "publishAPI": {"enabled": true}}}`, "")

	Context("Fresh cluster", func() {
		BeforeEach(func() {
			f.ValuesSet("global.modulesImages", GetModulesImages())
			f.KubeStateSet(``)
			testCreateJobPod()
			f.BindingContexts.Set(f.GenerateBeforeHelmContext())
			f.RunHook()
		})

		It("Certificate should be generated", func() {
			Expect(f.ValuesGet("userAuthn.internal.basicAuthProxyCert").String()).To(BeEquivalentTo(testingCert))
		})

		It("Should generate job with a valid image", func() {
			registry := f.ValuesGet("global.modulesImages.registry.base").String()
			digest := f.ValuesGet("global.modulesImages.digests.userAuthn.cfssl").String()
			job := generateJob(registry, digest, "dGVzdAo=")
			Expect(job.Spec.Template.Spec.Containers).To(HaveLen(1))
			Expect(job.Spec.Template.Spec.Containers[0].Image).To(ContainSubstring("@"))
			Expect(job.Spec.Template.Spec.Containers[0].Image).To(Equal(registry + "@" + digest))
		})
	})

	Context("Cluster with existing cert", func() {
		existingCert := ""
		existingKey := ""
		BeforeEach(func() {
			f.KubeStateSet(fmt.Sprintf(`
apiVersion: v1
kind: Secret
type: Opaque
data:
  client.crt: %s
  client.key: %s
metadata:
  name: basic-auth-cert
  namespace: d8-user-authn
`, existingCert, existingKey))
			f.BindingContexts.Set(f.GenerateBeforeHelmContext())
			f.RunHook()
		})

		It("Should keep previous certificate and key", func() {
			Expect(f.ValuesGet("userAuthn.internal.basicAuthProxyCert").String()).To(BeEquivalentTo(existingCert))
			Expect(f.ValuesGet("userAuthn.internal.basicAuthProxyKey").String()).To(BeEquivalentTo(existingKey))
		})
	})

	Context("Cluster with expired cert", func() {
		BeforeEach(func() {
			f.KubeStateSet(`
apiVersion: v1
kind: Secret
type: Opaque
data:
  client.crt: 
  client.key: 
metadata:
  name: basic-auth-cert
  namespace: d8-user-authn
`)
			f.BindingContexts.Set(f.GenerateBeforeHelmContext())
			testCreateJobPod()
			f.RunHook()
		})

		It("New cert should be generated", func() {
			Expect(f.ValuesGet("userAuthn.internal.basicAuthProxyCert").String()).To(BeEquivalentTo(testingCert))
		})
	})
})

func testCreateJobPod() {
	_, _ = dependency.TestDC.MustGetK8sClient().CoreV1().Pods("d8-system").Create(context.Background(), &corev1.Pod{
		TypeMeta: v1.TypeMeta{
			Kind:       "Pod",
			APIVersion: "v1",
		},
		ObjectMeta: v1.ObjectMeta{
			Name:      "testpod",
			Namespace: "d8-system",
			Labels: map[string]string{
				"job-name": proxyJobName,
			},
		},
	}, v1.CreateOptions{})
}
