targets.expr:
sum by (pod, node) (
  sum by (pod, node) (avg_over_time(kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}[$__range])) * on (pod, node) group_right() avg_over_time(kube_pod_info{node=~"$node", namespace="$namespace", pod=~"$pod"}[$__range])
)

sum by(pod) (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod)
  sum by (pod) (rate(container_cpu_usage_seconds_total{node=~"$node", container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)

sum (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod)
  sum by (pod) (rate(container_cpu_system_seconds_total{node=~"$node", container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)

sum (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod)
  sum by (pod) (rate(container_cpu_user_seconds_total{node=~"$node", container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)

sum by (pod)
  (
    kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
    * on (pod)
    sum by (pod) (
      sum by(namespace, pod, container) (avg_over_time(kube_pod_container_resource_requests{resource="cpu",unit="core",node=~"$node", namespace="$namespace", pod=~"$pod"}[$__rate_interval]))
      -
      sum by(namespace, pod, container) (rate(container_cpu_usage_seconds_total{node=~"$node", container!="POD", namespace="$namespace", pod=~"$pod"}[$__rate_interval]))
    ) > 0
  )

    (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod)
  sum by (pod) (
    (
      sum by(namespace, pod, container) (rate(container_cpu_usage_seconds_total{node=~"$node", namespace="$namespace", pod=~"$pod"}[$__rate_interval]))
      -
      sum by(namespace, pod, container) (avg_over_time(kube_pod_container_resource_requests{resource="cpu",unit="core",node=~"$node", namespace="$namespace", pod=~"$pod", container!="POD"}[$__rate_interval]))
    )
    or
    sum by(namespace, pod, container) (rate(container_cpu_usage_seconds_total{node=~"$node", namespace="$namespace", pod=~"$pod"}[$__rate_interval]))
  )
) > 0

sum by (pod)
(
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (controller_type, controller_name) group_left()
  sum by (controller_type, controller_name) (avg_over_time(vpa_target_recommendation{container!="POD", namespace="$namespace", resource="cpu"}[$__rate_interval]))
)

sum by(pod) (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod)
  sum by (pod) (avg_over_time(container_memory_working_set_bytes:without_kmem{node=~"$node", container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
