(
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod) group_left()
  sum by (pod)
    (
      (
        sum by (namespace, pod, container) (avg_over_time(kube_pod_container_resource_requests{resource="memory",unit="byte",node=~"$node", namespace="$namespace"}[$__rate_interval]))
        -
        sum by (namespace, pod, container) (avg_over_time(container_memory_working_set_bytes:without_kmem{node=~"$node", namespace="$namespace", container!="POD"}[$__rate_interval]))
      ) > 0
    )
)

(
  kube_controller_pod{node=~"$node", namespace="$namespace", controller="$controller", pod=~"$pod"}
  * on (pod) group_left()
  sum by (pod)
    (
      (
        (
          sum by (namespace, pod, container) (avg_over_time(container_memory_working_set_bytes:without_kmem{node=~"$node", namespace="$namespace"}[$__rate_interval]))
          -
          sum by (namespace, pod, container) (avg_over_time(kube_pod_container_resource_requests{resource="memory",unit="byte",node=~"$node", namespace="$namespace", container!="POD"}[$__rate_interval]))
        ) or sum by (namespace, pod, container) (avg_over_time(container_memory_working_set_bytes:without_kmem{node=~"$node", namespace="$namespace", container!="POD"}[$__rate_interval]))
      ) > 0
    )
