sum by(controller) (  # Data rate of the controller is a sum of data rates from its Pods.
  kube_controller_pod{node=~"$node", namespace="$namespace", controller_type=~"$controller_type", controller=~"$controller"} 
  * on(pod)
  group_left() # Do not drop controller label from kube_controller_pod.
  sum by(pod)  # Use sum if there are multiple interaces in the Pod.
  (
    rate(container_network_receive_bytes_total{node=~"$node", namespace="$namespace"}[$__range])
    *   # Select only Pods with 'hostNetwork: false', because receive_bytes values are meaningful only for Pods with hostNetwork: false.
    on(pod)
    kube_pod_info{host_network="false", namespace="$namespace"}
  )
)
or # Return -1 value for Pods with 'hostNetwork: true' to rewrite by value mapping.
# Use max to get one '-1' per controller
(max by(controller) (
  kube_controller_pod{node=~"$node", namespace="$namespace", controller_type=~"$controller_type", controller=~"$controller"}
  * on(pod)
  group_left() # Do not drop controller label from kube_controller_pod.
  (kube_pod_info{host_network="true", namespace="$namespace"})
) * -1)

sum by(controller) (  # Data rate of the controller is a sum of data rates from its Pods.
  kube_controller_pod{node=~"$node", namespace="$namespace", controller_type=~"$controller_type", controller=~"$controller"} 
  * on(pod)
  group_left() # Do not drop controller label from kube_controller_pod.
  sum by(pod)  # Use sum if there are multiple interaces in the Pod.
  (
    rate(container_network_transmit_bytes_total{node=~"$node", namespace="$namespace"}[$__range])
    *   # Select only Pods with 'hostNetwork: false', because receive_bytes values are meaningful only for Pods with hostNetwork: false.
    on(pod)
    kube_pod_info{host_network="false", namespace="$namespace"}
