      (
        (
          (
            sum by(namespace, pod, container) (rate(container_cpu_usage_seconds_total{node=~"$node", namespace="$namespace"}[$__rate_interval]))
            -
            sum by(namespace, pod, container) (avg_over_time(kube_pod_container_resource_requests{resource="cpu",unit="core",node=~"$node", container!="POD", namespace="$namespace"}[$__rate_interval]))
          ) or sum by(namespace, pod, container) (rate(container_cpu_usage_seconds_total{node=~"$node", container!="POD", namespace="$namespace"}[$__rate_interval]))
        ) > 0
      )
  )

