transform/destination/test-es-dest/00_el.source:
if exists(.pod_labels) {
    .pod_labels = map_keys(object(.pod_labels), recursive: true) -> |key| { replace(key, ".", "_") }
}

transform/source/test-source/00_clean_up.source:
if exists(.pod_labels."controller-revision-hash") {
    del(.pod_labels."controller-revision-hash")
}
if exists(.pod_labels."pod-template-hash") {
    del(.pod_labels."pod-template-hash")
}
if exists(.kubernetes) {
    del(.kubernetes)
}
if exists(.file) {
    del(.file)
}

transform/source/test-source/01_parse_js.source:
if !exists(.parsed_data) {
    structured, err = parse_json(.message)
    if err == null {
        .parsed_data = structured
    } else {
        .parsed_data = .message
    }
}
