transform/File/destination/test-socket2-.source:
sd_params = []

if exists(.file) && !is_null(.file) {
  sd_params = append(sd_params, ["file" + "=\"" + to_string!(.file) + "\""])
}
if exists(.host) && !is_null(.host) {
  sd_params = append(sd_params, ["host" + "=\"" + to_string!(.host) + "\""])
}
if exists(.host_ip) && !is_null(.host_ip) {
  sd_params = append(sd_params, ["host_ip" + "=\"" + to_string!(.host_ip) + "\""])
}
.sd_labels = if length(sd_params) > 0 { "[" + "deckhouse@0" + " " + join!(sd_params, separator: " ") + "]" }

if !exists(.syslog.severity) {
  .syslog.severity = 6;
} else if is_string(.syslog.severity) {
  .syslog.severity = to_syslog_severity!(.syslog.severity);
} else {
  .syslog.severity = 6;
};

pri = 1 * 8 + .syslog.severity;

., err = join([
  "<" + to_string(pri) + ">" + "1",     # <pri>version
  to_string!(.timestamp),
  to_string!(.pod_name || .host || "${VECTOR_SELF_NODE_NAME}"),  # hostname
  to_string!(.app || .pod_labels.app || .syslog.app || "-"), # app-name
  "-", # procid
  to_string!(.syslog.message_id || "-"), # msgid
  to_string!(.sd_labels || "-"), # structured-data
  decode_base16!("EFBBBF") + to_string!(.message || encode_json(.)) # msg
], separator: " ")

if err != null {
  log("Unable to construct syslog message for event:" + err + ". Dropping invalid event: " + encode_json(.), level: "error", rate_limit_secs: 10)
}

