transform/destination/test-socket2-dest/.source:
if !exists(.syslog.severity) {
  .syslog.severity = 6;
} else if is_string(.syslog.severity) {
  .syslog.severity = to_syslog_severity!(.syslog.severity);
} else {
  .syslog.severity = 6;
};

pri = 1 * 8 + .syslog.severity;

# Build structured-data from extra_labels (RFC5424 format)
structured_data = "-";
if exists(.syslog.extra_labels) && is_object(.syslog.extra_labels) {
  params = [];
  for_each(object!(.syslog.extra_labels)) -> |key, value| {
    # Escape quotes in key and value for RFC5424
    escaped_key = replace(to_string(key), r'"', r'\"');
    escaped_value = replace(to_string(value), r'"', r'\"');
    params = push(params, escaped_key + "=\"" + escaped_value + "\"");
  }
  if length(params) > 0 {
    structured_data = "[extraLabels " + join!(params, separator: " ") + "]";
  }
}

., err = join([
  "<" + to_string(pri) + ">" + "1",     # <pri>version
  to_string!(.timestamp),
  to_string!(.pod || .hostname || "${VECTOR_SELF_NODE_NAME}"),
  to_string!(.app || .pod_labels.app || .syslog.app || "-"),
  "-", # procid
  to_string!(.syslog.message_id || "-"), # msgid
  structured_data, # structured-data with extraLabels
  decode_base16!("EFBBBF") + to_string!(.message || encode_json(.)) # msg
], separator: " ")

if err != null {
  log("Unable to construct syslog message for event:" + err + ". Dropping invalid event: " + encode_json(.), level: "error", rate_limit_secs: 10)
}

transform/destination/test-socket3-dest/.source:
if !exists(.parsed_data) {
    structured, err = parse_json(.message)
    if err == null {
        .parsed_data = structured
    } else {
        .parsed_data = .message
    }
}

.cef.name="d8" 
 .cef.severity="1"

transform/destination/test-socket4-dest/.source:
if !exists(.cef) {
  .cef = {};
};

if !exists(.cef.name) {
  .cef.name = "Deckhouse Event";
};

if !exists(.cef.severity) {
  .cef.severity = "5";
} else if is_string(.cef.severity) {
  if .cef.severity == "Debug" {
    .cef.severity = "0";
  };
  if .cef.severity == "Informational" {
    .cef.severity = "3";
  };
  if .cef.severity == "Notice" {
    .cef.severity = "4";
  };
  if .cef.severity == "Warning" {
    .cef.severity = "6";
  };
  if .cef.severity == "Error" {
    .cef.severity = "7";
  };
  if .cef.severity == "Critical" {
    .cef.severity = "8";
  };
  if .cef.severity == "Emergency" {
    .cef.severity = "10";
  };
};

transform/source/test-source/00_local_ti.source:
if exists(."timestamp") {
    ts = parse_timestamp!(."timestamp", format: "%+")
    ."timestamp" = format_timestamp!(ts, format: "%+", timezone: "local")
}

if exists(."timestamp_end") {
    ts = parse_timestamp!(."timestamp_end", format: "%+")
    ."timestamp_end" = format_timestamp!(ts, format: "%+", timezone: "local")
}

transform/source/test-source/01_clean_up.source:
if exists(.pod_labels."controller-revision-hash") {
    del(.pod_labels."controller-revision-hash")
}
if exists(.pod_labels."pod-template-hash") {
    del(.pod_labels."pod-template-hash")
}
if exists(.kubernetes) {
    del(.kubernetes)
}
if exists(.file) {
    del(.file)
}
if exists(.node_labels."node.deckhouse.io/group") {
	.node_group = (.node_labels."node.deckhouse.io/group")
}
del(.node_labels)
