transform/destination/test-loki-dest/00_.source:
if exists(.message) && is_string(.message) {

  .message = replace_with_regex(.message, r'password=\w+', "password=***")
  .message = replace_with_regex(.message, r'token:\s*[\w\-]+', "token: ***")
  .message = replace_with_regex(.message, r'api_key=[\w\-]+', "api_key=***")
}

transform/source/test-source/00_local_ti.source:
if exists(."timestamp") {
    ts = parse_timestamp!(."timestamp", format: "%+")
    ."timestamp" = format_timestamp!(ts, format: "%+", timezone: "local")
}

if exists(."timestamp_end") {
    ts = parse_timestamp!(."timestamp_end", format: "%+")
    ."timestamp_end" = format_timestamp!(ts, format: "%+", timezone: "local")
}

transform/source/test-source/01_clean_up.source:
if exists(.pod_labels."controller-revision-hash") {
    del(.pod_labels."controller-revision-hash")
}
if exists(.pod_labels."pod-template-hash") {
    del(.pod_labels."pod-template-hash")
}
if exists(.kubernetes) {
    del(.kubernetes)
}
if exists(.file) {
    del(.file)
}
if exists(.node_labels."node.deckhouse.io/group") {
	.node_group = (.node_labels."node.deckhouse.io/group")
}
del(.node_labels)
